disp('Testing BuTools function PmfFromMG')
disp('Input:');
disp('------');
a = [-0.6,0.3,1.3];
disp('a = ');
disp(a);
A = [0.25, 0.2, -0.15; 0.3, 0.1, 0.25; 0, 0.2, 0.47];
disp('A = ');
disp(A);
x = (0.0:1.0:100.0);
disp('Test:');
disp('-----');
disp('pmf = PmfFromMG(a, A, x);:');
pmf = PmfFromMG(a, A, x);
plot(x, pmf)
assert(all(pmf>=0), 'PmfFromMG returned negative pmf!');
assert(abs(dot(x, pmf)-MomentsFromMG(a, A, 1))<10^-10, 'The mean computed from the pmf does not match the theoretical result!');
